import boto3
import os
import random
import json

# Initialize S3 client
s3_client = boto3.client('s3',
    region_name=os.environ.get('AWS_REGION')
)

UPLOAD_BUCKET = os.environ.get('INPUT_BUCKET')  # Replace this value with your bucket name!
URL_EXPIRATION_SECONDS = 300     # Specify how long the pre-signed URL will be valid for

def lambda_handler(event, context):
    file_name = event['queryStringParameters']['file']
    claim_id = event['queryStringParameters']['claim_id']
    # Generate a random filename
    key = f"{claim_id}/{file_name}"  # Random filename we will use when uploading files

    # Parameters for the pre-signed URL
    s3_params = {
        'Bucket': UPLOAD_BUCKET,
        'Key': key,
        'ContentType': 'application/octet-stream',
        'Expires': URL_EXPIRATION_SECONDS
    }

    try:
        # Generate the presigned URL
        upload_url = s3_client.generate_presigned_url(
            ClientMethod='put_object',
            Params=s3_params
        )

        # Prepare the response
        response = {
            "statusCode": 200,
            "isBase64Encoded": False,
            "headers": {
                "Access-Control-Allow-Origin": "*"
            },
            "body": json.dumps({
                "uploadURL": upload_url,
                "filename": key
            })
        }

        return response

    except Exception as e:
        return {
            "statusCode": 500,
            "body": json.dumps({"error": str(e)})
        }

import boto3
import json

dynamodb = boto3.resource('dynamodb')
sfn = boto3.client('stepfunctions')

def lambda_handler(event, context):
    claim_id = event['pathParameters']['claimId']
    account_id = context.invoked_function_arn.split(":")[4]
    region = context.invoked_function_arn.split(":")[3]

    # Check if report exists (processing complete)
    table = dynamodb.Table('insuranceclaim-reports-json')
    response = table.get_item(Key={'claimId': claim_id})

    if 'Item' in response:
        return {
            'statusCode': 200,
            'body': json.dumps({
                'status': 'COMPLETED',
                'claimId': claim_id
            }),
            'headers': {
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Credentials': True
            }
        }

    # Check Step Functions execution status
    try:
        executions = sfn.list_executions(
            stateMachineArn=f"arn:aws:states:{region}:{account_id}:stateMachine:insuranceclaim-ClaimProcessing",
            maxResults=50
        )

        for execution in executions['executions']:
            exec_input = json.loads(sfn.describe_execution(executionArn=execution['executionArn'])['input'])
            if exec_input.get('claimId') == claim_id:
                if execution['status'] == 'FAILED':
                    return {
                        'statusCode': 200,
                        'body': json.dumps({
                            'status': 'FAILED',
                            'claimId': claim_id
                        }),
                        'headers': {
                            'Access-Control-Allow-Origin': '*',
                            'Access-Control-Allow-Credentials': True
                        }
                    }
                break
    except Exception as e:
        print(f"Error checking execution status: {e}")

    # Otherwise, still processing
    return {
        'statusCode': 200,
        'body': json.dumps({
            'status': 'PROCESSING',
            'claimId': claim_id
        }),
        'headers': {
            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Credentials': True
        }
    }

import json
import boto3
import os

s3_client = boto3.client('s3')

def lambda_handler(event, context):
    params = event.get('queryStringParameters', {})
    claim_id = params.get('claimId')
    file_name = params.get('fileName')

    if not claim_id or not file_name:
        return {
            'statusCode': 400,
            'body': json.dumps({'error': 'claimId and fileName required'}),
            'headers': {'Access-Control-Allow-Origin': '*'}
        }

    bucket = os.environ['INPUT_BUCKET']
    key = f"{claim_id}/{file_name}"

    try:
        url = s3_client.generate_presigned_url(
            'get_object',
            Params={'Bucket': bucket, 'Key': key},
            ExpiresIn=3600
        )
        return {
            'statusCode': 200,
            'body': json.dumps({'url': url}),
            'headers': {'Access-Control-Allow-Origin': '*'}
        }
    except Exception as e:
        return {
            'statusCode': 500,
            'body': json.dumps({'error': str(e)}),
            'headers': {'Access-Control-Allow-Origin': '*'}
        }
